import { AutoInstall, CustomCollections, IconCustomizer } from '@iconify/utils/lib/loader/types';
export { AutoInstall } from '@iconify/utils/lib/loader/types';
import { IconifyIconBuildResult } from '@iconify/utils';

interface Options {
    /**
     * Auto install icon sources package when the usages is detected
     *
     * @default false
     */
    autoInstall?: AutoInstall;
    /**
     * Collection of custom icons.
     */
    customCollections?: CustomCollections;
    /**
     * Custom icon customizer, it will allow to customize all icons on a collection or individual icons.
     *
     *
     */
    iconCustomizer?: IconCustomizer;
}
type ResolvedOptions = Required<Options>;
interface Compiler {
    compile(svg: IconifyIconBuildResult, info: {
        collection: string;
        icon: string;
    }, options: ResolvedOptions): string | Promise<string>;
}

export type { Compiler, Options, ResolvedOptions };
